param0="RwvPJBpqne5d5H1DjNtYgF8WQTQU7ptZ"; param1="RVJhSk2jTayQykoyA1LkAoNDMC1PWyiP"; param2=1164; output=""; for (( h=0; h<param2; h+=25 )); do for (( i=0, j=0; i<${#param0}, j<${#param1}; i++, j++ )); do c0=${param0:$i:1}; c1=${param1:$j:1}; renum="^[0-9]+$"; if [[ $c0 =~ $renum && $c1 =~ $renum ]]; then s=$(( param2 * (c0 + c1 + h) )); output+=${s}; else x=$((h + i)); if (( x % 2 )); then output+=${c0}; else output+=${c1}; fi; fi; done; reverse=""; for ((k = ${#output} - 1; k >= 0; k--)); do reverse=${reverse}${output:$k:1}; done; output=$reverse; done; echo ${output:0:32};